def journalist_number(tab)
	puts "Le nombre de journalistes contenus dans le tableau est de : #{tab.size}"	
end

def handle_contains_number(tab)
	handle_chiffre = 0
	tab.each do |handle|
		if (handle.count "0-9") > 0
			handle_chiffre += 1
		end
	end
	puts "Le nombre de handle contenant un chiffre est de : #{handle_chiffre}"
end

def handle_contains_aude(tab)
	aude_tab = []
	to_find = "Aude"
	downcased = tab.map(&:downcase)
	downcased.each do |handle|
		if handle.include?(to_find.downcase) == true
			aude_tab << handle
		end
	end
	puts "Le nombre de handles contenant \"aude\" (sans prendre en compte la casse) est de : #{aude_tab.size}"
end

def handle_starts_uppercase(tab)
	starts_uppercase_tab = []
	tab.each do |handle|
		handle_tab = handle.split(//)
		if handle_tab[1] >= 'A' && handle_tab[1] <= 'Z'
			starts_uppercase_tab << handle
		end	
	end	
	puts "Le nombre de handle commencant par une majuscule apres l'@ est de : #{starts_uppercase_tab.size}."
end

def handle_contains_uppercase(tab)
	contains_uppercase_tab = []
	tab.each do |handle|
	if handle =~ /[A-Z]/
		contains_uppercase_tab << handle	
		end
	end
	puts "Le nombre de handle contenant une majuscule est de : #{contains_uppercase_tab.size}"
end

def _number(tab)
	number_of_ = 0
	char_tab = []
	tab.each do |handle|
		char_tab = handle.split(//)
		char_tab.each do |c|
			if c == '_'
				number_of_ += 1	
			end
		end
	end
	puts "Le nombre de caracteres '_' est de : #{number_of_}"
end

def sort_alphabetical_order(tab)
	tab = tab.sort
	return tab
end

def shortest_fifty(tab)
	tab = tab.sort_by(&:length)
	i = 0
	puts "Les 50 handles les plus courts sont :"
	while (i <= 49) do
		print "#{tab[i]} "
		i = i + 1
	end
	print "\n"
end

def find_user(tab, str)
	n_index = tab.index(str)
	return n_index
end

